package WhileLoopDemonstration;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int tech =1;
		switch(tech) {
		
		case 1:
		{
			System.out.println("JAVA");
			break;		
		}
		case 2:
		{
			System.out.println("python");
			break;
		}
		default:
		{
			System.out.println("Not Listed..");
		}
			
	}

}
}